// for short cut use rsf
import React from 'react';
import styled from 'styled-components';

function Header() {
  return (
    <Nav>
    <Logo src="/images/logo.svg" />
    <NavMenu>
    <a>
    <img src="/images/home-icon.svg" />
    <span> Home </span>
    </a>
    <a>
    <img src="/images/search-icon.svg" />
    <span> Search </span>
    </a>
    <a>
    <img src="/images/watchlist-icon.svg" />
    <span> Watchlist </span>
    </a>
    <a>
    <img src="/images/original-icon.svg" />
    <span> Originals </span>
    </a>
    <a>
    <img src="/images/movie-icon.svg" />
    <span> Movies </span>
    </a>
    <a>
    <img src="/images/series-icon.svg" />
    <span> Series </span>
    </a>
    </NavMenu>
    <UserImg src="/images/RenP.png" />
    </Nav>
    );
  }

  export default Header;

  const Nav = styled.nav`
  height: 70px;
  background: #090b13;
  display: flex;
  align-items: center;
  padding: 0 36px;
  `

  const Logo = styled.img`
  width: 80px;

  `

  // flex 1 makes the navbar the most important component and pushes everything aside
  const NavMenu = styled.div`
  display: flex;
  flex: 1;
  margin-left: 20px;
  cursor: pointer;
  align-items: center;

  a {
    display: flex;
    align-items: center;
    padding: 0 12px;

    img{
      height: 20px;
    }

    span {
      font-size: 13px;
      letter-spacing: 1.42px;
      position: relative;

      &:after {
        content: "";
        height: 2px;
        background: white;
        position: absolute;
        left: 0;
        right: 0;
        bottom: -6px;
        opactity: 0;
        transform: scaleX(0);
      }
    }

    &:hover {
      span: afer
    }
  }
  `
  // the styled component is what you want the html to be, in this case, img tag

  const UserImg = styled.img`
  width: 48px;
  height: 48px;
  border-radius: 50%;
  cursor: pointer;

  `
